import React, { useEffect, useState } from 'react';
import axios from 'axios';

function ViewDistributionPage() {
  const [distributionData, setDistributionData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDistribution = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/lists/by-agent', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setDistributionData(res.data);
      } catch (err) {
        console.error('Error fetching distribution:', err);
        alert('Failed to fetch distribution');
      } finally {
        setLoading(false);
      }
    };

    fetchDistribution();
  }, []);

  if (loading) return <p>Loading...</p>;

  return (
    <div style={{ padding: '20px' }}>
      <h2>Distribution by Agent</h2>
      {distributionData.length === 0 ? (
        <p>No distributions found.</p>
      ) : (
        distributionData.map((agent) => (
          <div key={agent.agentId} style={{ marginBottom: '20px' }}>
            <h3>Agent: {agent.agentName || agent.agentId}</h3>
            <ul>
              {agent.lists.map((list, idx) => (
                <li key={idx}>{list}</li>
              ))}
            </ul>
          </div>
        ))
      )}
    </div>
  );
}

export default ViewDistributionPage;
